var fs = require('fs');
var path = require('path');
var ms = require('memspace');
//var express = require('express');

module.exports = memspace;

memspace.path = null;
memspace.pathName = null;

memspace.setPath = function(_path) {
    if (fs.existsSync(_path)) {
        this.path = _path;
    }
    else {
        throw 'Path to memspace folder does not exist: ' + _path;
    }
    return this;
};

memspace.getPath = function() {
    if (this.path === null) {
        throw 'Path to memspace folder not set.';
    }
    return this.path;
};

memspace.setName = function(name) {
    if (typeof(name) === 'undefined' || !name.length) {
        throw 'Memspace name cannot be empty';
    }
    else {
        var pathName = path.join(this.getPath(), name);
        if (fs.existsSync(pathName)) {
            this.pathName = pathName;
        }
        else {
            throw 'Memspace name does not exist: ' + name;
        }
    }
    return this;
};

memspace.getPathName = function() {
    if (this.pathName === null) {
        throw 'PathName to memspace folder not set.';
    }
    return this.pathName;
};

